import { useEffect, useState } from 'react'

export default function AccessibilitySettings() {
  const [settings, setSettings] = useState(() => ({
    fontSize: localStorage.getItem('fontSize') || 'medium',
    highContrast: localStorage.getItem('highContrast') === 'true',
    voiceNavigation: false
  }))

  useEffect(() => {
    // Apply font size
    document.documentElement.style.fontSize = 
      settings.fontSize === 'large' ? '20px' :
      settings.fontSize === 'medium' ? '16px' : '14px'

    // Apply high contrast
    if (settings.highContrast) {
      document.documentElement.classList.add('high-contrast')
    } else {
      document.documentElement.classList.remove('high-contrast')
    }

    // Save to localStorage
    localStorage.setItem('fontSize', settings.fontSize)
    localStorage.setItem('highContrast', settings.highContrast)
  }, [settings])

  return (
    <div className="relative group">
      <button 
        aria-label="Accessibility Settings"
        className="p-2 rounded-full hover:bg-gray-100"
      >
        <svg className="w-6 h-6 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6V4m0 2a2 2 0 100 4m0-4a2 2 0 110 4m-6 8a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4m6 6v10m6-2a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4" />
        </svg>
      </button>

      <div className="hidden group-hover:block absolute right-0 mt-2 w-64 bg-white rounded-md shadow-lg p-4 space-y-4 z-50">
        <div>
          <label className="block text-sm font-medium text-gray-700">Text Size</label>
          <select
            value={settings.fontSize}
            onChange={(e) => setSettings(s => ({...s, fontSize: e.target.value}))}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-accessible-blue focus:ring-accessible-blue text-sm"
          >
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
        </div>

        <div className="flex items-center">
          <input
            type="checkbox"
            checked={settings.highContrast}
            onChange={(e) => setSettings(s => ({...s, highContrast: e.target.checked}))}
            className="h-4 w-4 text-accessible-blue focus:ring-accessible-blue border-gray-300 rounded"
          />
          <label className="ml-2 text-sm text-gray-700">High Contrast Mode</label>
        </div>

        <div className="flex items-center">
          <input
            type="checkbox"
            checked={settings.voiceNavigation}
            onChange={(e) => setSettings(s => ({...s, voiceNavigation: e.target.checked}))}
            className="h-4 w-4 text-accessible-blue focus:ring-accessible-blue border-gray-300 rounded"
          />
          <label className="ml-2 text-sm text-gray-700">Voice Navigation</label>
        </div>
      </div>
    </div>
  )
}
