import { useState } from 'react';
import { db } from '../firebase';
import { doc, setDoc } from 'firebase/firestore';
import { useAuth } from '../contexts/AuthContext';

export default function ProfileForm() {
  const { currentUser } = useAuth();
  const [formData, setFormData] = useState({
    skills: [],
    accessibilityNeeds: [],
    bio: '',
    resumeUrl: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await setDoc(doc(db, 'users', currentUser.uid), formData);
      alert('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6 max-w-3xl mx-auto">
      <div>
        <label className="block text-sm font-medium text-gray-700">Skills</label>
        <input
          type="text"
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
          value={formData.skills.join(', ')}
          onChange={(e) => setFormData({...formData, skills: e.target.value.split(', ')})}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700">Accessibility Needs</label>
        <div className="mt-2 space-y-2">
          {['Wheelchair Access', 'Screen Reader', 'Sign Language', 'Flexible Hours'].map((need) => (
            <label key={need} className="inline-flex items-center mr-4">
              <input
                type="checkbox"
                checked={formData.accessibilityNeeds.includes(need)}
                onChange={(e) => {
                  const needs = e.target.checked
                    ? [...formData.accessibilityNeeds, need]
                    : formData.accessibilityNeeds.filter(n => n !== need);
                  setFormData({...formData, accessibilityNeeds: needs});
                }}
                className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
              />
              <span className="ml-2 text-sm text-gray-700">{need}</span>
            </label>
          ))}
        </div>
      </div>

      <button
        type="submit"
        className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
      >
        Save Profile
      </button>
    </form>
  );
}
